package ai.samepage.service;

import ai.samepage.common.core.util.JacksonUtils;
import ai.samepage.common.redis.service.LockService;
import ai.samepage.constant.Constants;
import ai.samepage.entity.BotInfo;
import ai.samepage.entity.BotTypeEnum;
import ai.samepage.entity.SupportedTypeEnum;
import ai.samepage.llm.LlmGatewayService;
import ai.samepage.llm.model.Assistant;
import ai.samepage.llm.model.OpenAiResponse;
import cn.hutool.core.collection.CollectionUtil;
import cn.hutool.core.thread.NamedThreadFactory;
import cn.hutool.core.util.IdUtil;
import cn.hutool.core.util.StrUtil;
import com.baomidou.mybatisplus.core.toolkit.Wrappers;
import com.google.common.collect.Lists;
import lombok.extern.slf4j.Slf4j;
import org.springframework.data.redis.core.RedisTemplate;
import org.springframework.scheduling.annotation.Scheduled;

import javax.annotation.PostConstruct;
import javax.annotation.Resource;
import java.time.LocalDateTime;
import java.util.Collections;
import java.util.List;
import java.util.Objects;
import java.util.concurrent.locks.Lock;
import java.util.stream.Collectors;

/**
 * ScheduledAssistant
 */
//@Component
@Slf4j
public class ScheduledAssistant {
    @Resource
    private LlmGatewayService llmGatewayService;


    @Resource
    private LockService lockService;

    @Resource
    private BotService botService;

    @Resource
    private RedisTemplate redisTemplate;


    private static final String  ICON = "data:image/png;base64,";


    /**
     * Executes a scheduled task that runs every 5 minutes.
     *
     * @throws Exception If any error occurs during the execution of the task.
     */
    @Scheduled(initialDelay = 1000 * 5, fixedRate = 1000 * 30) // Cron expression to run the task every 5 minutes
    @PostConstruct
    public void executeTask() throws Exception {
        NamedThreadFactory factory = new NamedThreadFactory("assistant", false);
        factory.newThread(() -> {
            while (true) {
                try {
                    log.info("开始同步 assistant api 的assistant,{}", System.currentTimeMillis());
                    Lock lock = lockService.getLock("assistant:init");
                    if (lock.tryLock()) {
                        try {
                            List<BotInfo> botInfos = botService.getBaseMapper().selectList(
                                    Wrappers.lambdaQuery(
                                                    BotInfo.class
                                            ).eq(BotInfo::getIsDeleted, false)
                                            .eq(BotInfo::getType, BotTypeEnum.assistant_chat)
                            );
                            List<String> assistants = botInfos.stream().map(
                                    BotInfo::getKey
                            ).collect(Collectors.toList());
                            List<Assistant> addAssistant = Lists.newArrayList();
                            //List<Assistant> updateAssistant = Lists.newArrayList();
                            OpenAiResponse<Assistant> assistantOpenAiResponse = null;
                            String assistantId = null;
                            boolean refresh = false;
                            //do {
                            assistantOpenAiResponse = llmGatewayService.listAssistants(assistantId, 100,null);
                            if (Objects.nonNull(assistantOpenAiResponse)) {
                                List<Assistant> data = assistantOpenAiResponse.getData();
                                if (CollectionUtil.isNotEmpty(data)) {
                                    data.forEach(d -> {
                                        if (CollectionUtil.isNotEmpty(assistants) && assistants.contains(d.getId())) {
                                            assistants.remove(d.getId());
                                        } else {
                                            addAssistant.add(d);
                                        }
                                    });
                                }
                            }
                            if (CollectionUtil.isNotEmpty(addAssistant)) {
                                log.info("开始添加");
                                refresh = true;
                                List<BotInfo> infos = addAssistant.stream().map(
                                        k -> {
                                            log.info("开始添加：{}:{}", k.getId(), k.getName());
                                            BotInfo info = new BotInfo();
                                            info.setId(IdUtil.getSnowflakeNextId());
                                            info.setName(k.getName());
                                            String description = k.getDescription();
                                            if (StrUtil.isBlank(description)) {
                                                description = k.getInstructions();
                                            }
                                            if (StrUtil.isBlank(description)) {
                                                description = k.getName();
                                            }
                                            info.setDesc(description);
                                            info.setType(BotTypeEnum.assistant_chat);
                                            info.setUpdateTime(LocalDateTime.now());
                                            info.setCreateId(1L);
                                            info.setIsStream(false);
                                            info.setCategoryKey("assistant_bot");
                                            info.setKey(k.getId());
                                            info.setSort(k.getCreatedAt());
                                            info.setBotType("bot");
                                            info.setIcon(ICON);
                                            info.setSidebar(ICON);
                                            info.setSystem(false);
                                            info.setFeature(JacksonUtils.writeValueAsString(Collections.singletonList("text_input")));
                                            info.setH5(SupportedTypeEnum.supported);
                                            info.setWeb(SupportedTypeEnum.supported);
                                            info.setExtension(SupportedTypeEnum.supported);
                                            info.setCreateTime(LocalDateTime.now());
                                            return info;
                                        }
                                ).collect(Collectors.toList());
                                botService.saveBatch(infos);
                            }
                           /* if (CollectionUtil.isNotEmpty(updateAssistant)) {
                                log.info("开始更新");
                                updateAssistant.forEach(
                                        k ->
                                                botService.update(
                                                        Wrappers.lambdaUpdate(BotInfo.class)
                                                                .eq(BotInfo::getKey, k.getId())
                                                                .set(BotInfo::getName, k.getName())
                                                                .set(BotInfo::getIcon, ICON)
                                                                .set(BotInfo::getSidebar, ICON)
                                                                .set(BotInfo::getFeature, JacksonUtils.writeValueAsString(Collections.singletonList("text_input")))
                                                                .set(BotInfo::getUpdateTime, LocalDateTime.now())
                                                                .eq(BotInfo::getIsDeleted, false)
                                                                .eq(BotInfo::getType, BotTypeEnum.assistant_chat)
                                                                .eq(BotInfo::getIsStream, false)
                                                                .eq(BotInfo::getCategoryKey, "assistant_bot")
                                                                .eq(BotInfo::getBotType, "bot")
                                                                .eq(BotInfo::getSystem, false))

                                );
                            }*/
                            if (CollectionUtil.isNotEmpty(assistants)) {
                                log.info("开始删除：{}", assistants);
                                refresh = true;
                                botService.update(
                                        Wrappers.lambdaUpdate(BotInfo.class)
                                                .in(BotInfo::getKey, assistants)
                                                .set(BotInfo::getIsDeleted, true)
                                                .set(BotInfo::getUpdateTime, LocalDateTime.now())
                                                .eq(BotInfo::getCategoryKey, "assistant_bot")
                                                .eq(BotInfo::getBotType, "bot")
                                                .eq(BotInfo::getSystem, false)
                                                .eq(BotInfo::getType, BotTypeEnum.assistant_chat)
                                );
                            }
                            //} while (Objects.nonNull(assistantOpenAiResponse) && Objects.equals(true, assistantOpenAiResponse.hasMore));
                            if (refresh) {
                                redisTemplate.delete(Constants.BOT_SYS_VERSION);
                            }
                            log.info("同步完成");
                        } finally {
                            lock.unlock();
                        }
                    }
                    Thread.sleep(600000);
                } catch (Exception e) {
                    log.error(e.getMessage(), e);
                }
            }

        }).start();


        // Example: perform database cleanup, send notifications, or refresh data from external APIs
    }
}
