CREATE TABLE IF NOT EXISTS `t_bot`
(
    `id`              bigint(20)   NOT NULL COMMENT '唯一索引',
    `name`            varchar(100) NOT NULL COMMENT '机器人的标题',
    `desc`            text         NOT NULL COMMENT '描述信息',
    `icon`            mediumblob COMMENT '图标的base64 字符串',
    `type`            varchar(64)  NOT NULL COMMENT '机器人的类型,聊天类型：chat;模板类型：template;draw：绘画类型',
    `system`          tinyint(1)   NOT NULL DEFAULT '0' COMMENT '是否固定在侧边栏',
    `feature`         varchar(512)          DEFAULT NULL COMMENT '模型的功能列表，目前有：快捷操作(intent),阅读全文(read_article),文档阅读（read_doc），跳转模型：message:redirect:[bot_key]',
    `welcome`         text COMMENT '会话的欢迎语以及感兴趣的问题，是一个json 字符串',
    `model_config`    text COMMENT '大模型的配置信息，目前（gpt3.5 定制）仅支持一个bot对应一个模型',
    `template_config` text COMMENT '模版的参数配置',
    `template_prompt` text COMMENT '模版的提示词，中间参数通过占位符进行占位，比如：{张三}',
    `key`             varchar(100) NOT NULL COMMENT '机器人的唯一标识',
    `create_time`     datetime     NOT NULL COMMENT '创建时间',
    `create_id`       bigint(20)            DEFAULT NULL COMMENT '创建人',
    `category_key`    varchar(64)  NOT NULL COMMENT '分类的key',
    `update_time`     datetime     NOT NULL COMMENT '更新时间',
    `sidebar`         mediumblob COMMENT '侧边图标',
    `lang`            text COMMENT '语言设置',
    `sort`            int(11)      NOT NULL COMMENT '排序',
    `is_deleted`      tinyint(1)   NOT NULL DEFAULT '0' COMMENT '是否删除',
    `bot_type`        varchar(32)  NOT NULL DEFAULT 'bot' COMMENT '机器人的类型',
    `is_stream`       tinyint(1)   NOT NULL DEFAULT '1' COMMENT '是否是流式',
    `feature_config`  text COMMENT '功能列表的配置信息',
    `h5`              varchar(32)  NOT NULL DEFAULT 'supported' COMMENT 'H5端支持的粒度，主要值有SUPPORTED（支持）, NOT_SUPPORTED（不支持）, FUTURE_SUPPORTED（未来支持）',
    `web`             varchar(32)  NOT NULL DEFAULT 'supported' COMMENT 'web端支持的粒度，主要值有SUPPORTED（支持）, NOT_SUPPORTED（不支持）, FUTURE_SUPPORTED（未来支持）',
    `extension`       varchar(32)  NOT NULL DEFAULT 'supported' COMMENT '插件端支持的粒度，主要值有SUPPORTED（支持）, NOT_SUPPORTED（不支持）, FUTURE_SUPPORTED（未来支持）',
    PRIMARY KEY (`id`),
    UNIQUE KEY `t_bot_key_IDX` (`key`) USING BTREE
) ENGINE = InnoDB
  DEFAULT CHARSET = utf8mb4 COMMENT ='机器人列表';

CREATE TABLE IF NOT EXISTS `t_category`
(
    `id`         int(11)     NOT NULL COMMENT '唯一标识，分类的排序',
    `key`        varchar(64) NOT NULL COMMENT '分类的唯一标识',
    `name`       varchar(64) NOT NULL COMMENT '分类的名称',
    `parent_key` varchar(64)          DEFAULT NULL COMMENT '父级的分类的key',
    `is_deleted` tinyint(4)  NOT NULL DEFAULT '0' COMMENT '是否删除',
    PRIMARY KEY (`id`),
    KEY `t_category_key_IDX` (`key`) USING BTREE
) ENGINE = InnoDB
  DEFAULT CHARSET = utf8mb4 COMMENT ='机器人分类';


CREATE TABLE IF NOT EXISTS `t_conversation_info`
(
    `id`                bigint(20)   NOT NULL COMMENT '唯一id',
    `title`             varchar(100) COMMENT '会话标题',
    `create_time`       datetime     NOT NULL COMMENT '创建时间',
    `update_time`       datetime     NOT NULL COMMENT '更新时间',
    `web_url`           varchar(1024) DEFAULT NULL COMMENT '来源的网站地址',
    `model`             varchar(100) NOT NULL COMMENT 'llm 模型',
    `type`              tinyint(2)   NOT NULL COMMENT '会话的类型，0：普通会话 1： gptindex',
    `create_id`         bigint(20)   NOT NULL COMMENT '创建的会话的员工id',
    `description`       text COMMENT '会话描述信息，这里指的是第一句话',
    `last_message_time` datetime     NOT NULL COMMENT '最近的添加会话的时间',
    thread_id           varchar(100) NULL COMMENT 'assistant 的thread id',
    `is_deleted`        tinyint(1)   NOT NULL COMMENT '是否删除',
    `doc_id`            bigint(20)    DEFAULT NULL COMMENT '文档id,当type字段值为2（DOC）时才有值',
    PRIMARY KEY (`id`),
    KEY `t_conversation_info_create_id_IDX` (`create_id`) USING BTREE,
    KEY `t_conversation_info_title_IDX` (`title`(100)) USING BTREE,
    KEY `t_conversation_info_doc_id_IDX` (`doc_id`) USING BTREE
) ENGINE = InnoDB
  DEFAULT CHARSET = utf8mb4 COMMENT ='会话的基本信息';

CREATE TABLE IF NOT EXISTS `t_conversation_message`
(
    `id`              bigint(20) unsigned NOT NULL COMMENT '自增主键',
    `message_id`      varchar(36)         NOT NULL COMMENT '消息id',
    `parent_id`       varchar(36)                  DEFAULT NULL COMMENT '消息父id（未null时表示根节点）',
    `content`         varchar(8096)                DEFAULT NULL COMMENT '内容',
    `content_type`    tinyint(2)                   DEFAULT '0' COMMENT '内容类型（0:文本，1：快捷操作2：阅读全文3.摘要及想要的问题 4.带来源的回答）',
    `role`            tinyint(2) unsigned          DEFAULT '0' COMMENT '1:用户，2:系统 3 助手',
    `conversation_id` bigint(20)                   DEFAULT NULL COMMENT '会话id',
    `create_id`       bigint(20) unsigned NOT NULL,
    `create_time`     datetime(3)         NOT NULL COMMENT '创建时间',
    `status`          tinyint(2)                   DEFAULT '0' COMMENT '错误状态（0：否，1：限流 2 服务异常）',
    `request_token`   int(11)                      DEFAULT NULL COMMENT '此次请求的token数，只会放在回答里面',
    `suggest`         text COMMENT '可能想问的问题列表',
    `type`            tinyint(1)                   DEFAULT NULL COMMENT '消息的类型（0：文本1：图片）',
    `reference`       text COMMENT '来源信息',
    `like`            varchar(100)                 DEFAULT NULL COMMENT '点赞或者踩（反馈信息）',
    `intent_id`       varchar(100)                 DEFAULT NULL COMMENT '快捷操作的第一个messageId',
    `response_token`  int(11)                      DEFAULT NULL COMMENT '返回的token 数',
    `model`           varchar(100)                 DEFAULT NULL COMMENT '使用的模型信息',
    `is_deleted`      tinyint(1)          NOT NULL DEFAULT '0' COMMENT '消息是否删除',
    `update_time`     datetime                     DEFAULT NULL COMMENT '更新时间',
    `long_content`    mediumtext COMMENT '长内容的存储',
    `source`          varchar(2048)                DEFAULT NULL COMMENT '来源的机器人唯一标识',
    `question`        text COMMENT '联网检索的问题',
    PRIMARY KEY (`id`),
    KEY `idx_message_id_parent_id_conversation_id_create_id` (`message_id`, `parent_id`, `conversation_id`, `create_id`,
                                                              `create_time`),
    KEY `t_conversation_message_conversation_id_IDX` (`conversation_id`) USING BTREE
) ENGINE = InnoDB
  DEFAULT CHARSET = utf8mb4 COMMENT ='会话消息表';



CREATE TABLE IF NOT EXISTS `t_document_info`
(
    `id`          bigint(20)   NOT NULL COMMENT '文档的id',
    `doc_name`    varchar(500) NOT NULL COMMENT '文件名',
    `create_id`   bigint(20)   NOT NULL COMMENT '上传者',
    `create_time` datetime     NOT NULL COMMENT '上传时间',
    `md5`         varchar(32)  NOT NULL COMMENT '文件的md5',
    `size`        int(11)      NOT NULL COMMENT '文件的大小，单位是B',
    `doc_type`    varchar(32)  NOT NULL COMMENT '文件的类型，目前仅是pdf',
    `obj_id`      varchar(225) NOT NULL COMMENT '文件的存储地址，这里指的是对象存储的obj_id',
    `is_deleted`  tinyint(1)   NOT NULL DEFAULT '0' COMMENT '是否删除',
    `status`      varchar(32)           DEFAULT NULL COMMENT '文档的状态，这里仅有running(正在执行),handler_success（执行完成，但是未拿结果）,success（执行完成）',
    `document_id` varchar(64)           DEFAULT NULL COMMENT '执行任务的documentId',
    `update_time` datetime     NOT NULL COMMENT '更新时间',
    PRIMARY KEY (`id`),
    KEY `t_document_info_create_id_IDX` (`create_id`) USING BTREE
) ENGINE = InnoDB
  DEFAULT CHARSET = utf8mb4 COMMENT ='文档的基本信息';


CREATE TABLE IF NOT EXISTS `t_document_summary`
(
    `id`          bigint(20)  NOT NULL COMMENT '唯一标识',
    `md5`         varchar(32) NOT NULL COMMENT '文件的md5',
    `language`    varchar(32) NOT NULL COMMENT '语言信息',
    `summary`     text        NOT NULL COMMENT '摘要信息',
    `questions`   text        NOT NULL COMMENT '可能想问的问题',
    `create_time` datetime    NOT NULL COMMENT '创建时间',
    `document_id` varchar(64) DEFAULT NULL COMMENT '执行任务的documentId',
    `status`      tinyint(2)  DEFAULT NULL COMMENT '文件的状态信息',
    PRIMARY KEY (`id`),
    UNIQUE KEY `t_document_summary_md5_IDX` (`md5`, `language`) USING BTREE
) ENGINE = InnoDB
  DEFAULT CHARSET = utf8mb4 COMMENT ='文档的摘要和可能问的问题的表';

CREATE TABLE IF NOT EXISTS `t_prompt_template`
(
    `id`          bigint(20) unsigned NOT NULL AUTO_INCREMENT,
    `name`        varchar(64)         NOT NULL COMMENT '名称',
    `desc`        varchar(256)        NOT NULL                                DEFAULT '' COMMENT '描述',
    `icon`        varchar(64)                                                 DEFAULT '' COMMENT '图标名称',
    `TYPE`        enum ('user_custom','official','quick_action','enterprise') DEFAULT 'user_custom' COMMENT '模板类型（user_custom-用户自定义提示词,official-官方提示词,quick_action-快捷指令提示词,enterprise-企业提示词）',
    `content`     varchar(3096)       NOT NULL                                DEFAULT '' COMMENT 'prompt具体内容',
    `is_public`   tinyint(2) unsigned                                         DEFAULT '0' COMMENT '是否公开',
    `is_deleted`  tinyint(2) unsigned                                         DEFAULT '0',
    `creator`     bigint(20)          NOT NULL COMMENT '创建者',
    `category`    varchar(64)                                                 DEFAULT NULL COMMENT '分类',
    `create_time` datetime                                                    DEFAULT CURRENT_TIMESTAMP,
    `update_time` datetime                                                    DEFAULT CURRENT_TIMESTAMP ON UPDATE CURRENT_TIMESTAMP,
    `tenant_id`   int(11)                                                     DEFAULT NULL COMMENT '企业的唯一标识',
    `user_name`   varchar(50)                                                 DEFAULT NULL COMMENT '昵称',
    `mail`        varchar(100)                                                DEFAULT NULL COMMENT '邮箱',
    PRIMARY KEY (`id`),
    KEY `idx_prompt_template_create_id` (`creator`),
    KEY `idx_prompt_template_is_public` (`is_public`)
) ENGINE = InnoDB
  DEFAULT CHARSET = utf8mb4 COMMENT ='prompt表';

CREATE TABLE IF NOT EXISTS `t_template_record`
(
    `id`          bigint(20)                     NOT NULL COMMENT '唯一标识',
    `bot_key`     varchar(64) CHARACTER SET utf8 NOT NULL COMMENT '机器人的主键',
    `create_id`   bigint(20)                     NOT NULL COMMENT '创建者的id',
    `create_time` datetime                       NOT NULL COMMENT '创建时间',
    `update_time` datetime                       NOT NULL COMMENT '更新时间',
    `is_deleted`  tinyint(4)                     NOT NULL DEFAULT '0' COMMENT '是否删除',
    `content`     text                           NOT NULL COMMENT '模板的参数设置',
    `md5`         varchar(64)                    NOT NULL COMMENT '参数的文本信息',
    PRIMARY KEY (`id`),
    KEY `t_template_record_bot_id_IDX` (`bot_key`, `create_id`) USING BTREE,
    KEY `t_template_record_md5_IDX` (`md5`) USING BTREE
) ENGINE = InnoDB
  DEFAULT CHARSET = utf8mb4 COMMENT ='模版的历史记录';



CREATE TABLE IF NOT EXISTS `t_user`
(
    `id`          bigint(20)  NOT NULL,
    `nick_name`   varchar(50) NOT NULL COMMENT '昵称',
    `avatar`      varchar(512)        DEFAULT NULL COMMENT '头像链接',
    `login_phone` varchar(20)         DEFAULT NULL COMMENT '登录手机号',
    `password`    varchar(64)         DEFAULT NULL COMMENT '密码',
    `is_deleted`  tinyint(4) unsigned DEFAULT '0' COMMENT '逻辑删除',
    `user_status` tinyint(4) unsigned DEFAULT '0' COMMENT '用户状态（0-未激活，1-激活）',
    `invite_code` varchar(4)          DEFAULT NULL COMMENT '邀请码（默认为null）',
    `tenant_id`   bigint(20)          DEFAULT NULL COMMENT '租户id',
    `create_time` datetime            DEFAULT CURRENT_TIMESTAMP,
    `update_time` datetime            DEFAULT CURRENT_TIMESTAMP ON UPDATE CURRENT_TIMESTAMP,
    `expire_time` datetime    NOT NULL COMMENT '失效时间（距离创建时间为七天的有效时间包含时分秒）',
    `mail`        varchar(100)        DEFAULT NULL COMMENT '邮箱',
    PRIMARY KEY (`id`),
    UNIQUE KEY `t_user_mail_IDX` (`mail`) USING BTREE
) ENGINE = InnoDB
  DEFAULT CHARSET = utf8mb4 COMMENT ='用户表';

CREATE TABLE IF NOT EXISTS `t_user_setting`
(
    `user_id`         bigint(20) unsigned NOT NULL,
    `expand_read_all` tinyint(1)   DEFAULT '1' COMMENT '是否展示阅读全文的图标',
    llm_base_url varchar(225) NOT NULL COMMENT 'llm 的base url',
    llm_token varchar(100) NOT NULL COMMENT 'llm token',
    create_time DATETIME NULL COMMENT '创建时间',
    update_time DATETIME NULL COMMENT '更新时间',
    is_system TINYINT(1) DEFAULT true NOT NULL COMMENT '是否是系统的设置，false 是用户自定义',
    assistant_token varchar(100) NOT NULL COMMENT 'assistant_token',
    PRIMARY KEY (`user_id`)
) ENGINE = InnoDB
  DEFAULT CHARSET = utf8mb4 COMMENT ='用户设置表';




CREATE TABLE IF NOT EXISTS `t_conv_msg_statistics`
(
    `id`             bigint(20)  NOT NULL COMMENT '唯一标识',
    `model`          varchar(64) NOT NULL COMMENT 'llm 大模型',
    `conv_id`        bigint(21)  NOT NULL COMMENT '会话id',
    `msg_id`         varchar(64) NOT NULL COMMENT '消息id',
    `request_token`  int(11) DEFAULT NULL COMMENT '请求的token 数',
    `response_token` int(11) DEFAULT NULL COMMENT '返回的token 数',
    `create_id`      bigint(20)  NOT NULL COMMENT '创建者',
    `create_time`    datetime    NOT NULL COMMENT '创建时间',
    PRIMARY KEY (`id`)
) ENGINE = InnoDB
  DEFAULT CHARSET = utf8mb4 COMMENT ='llm 大模型统计表';
CREATE TABLE IF NOT EXISTS `t_bot_user`
(
    `id`          bigint(20)  NOT NULL COMMENT '唯一标识',
    `user_id`     bigint(20)  NOT NULL COMMENT '用户id',
    `bot_key`     varchar(64) NOT NULL COMMENT '机器人id',
    `is_deleted`  tinyint(1)  NOT NULL DEFAULT '0' COMMENT '是否删除',
    `fixed`       tinyint(1)  NOT NULL DEFAULT '0' COMMENT '是否固定在菜单上面',
    `create_time` datetime    NOT NULL COMMENT '创建时间',
    `update_time` datetime    NOT NULL COMMENT '更新时间',
    `updater`     bigint(20)           DEFAULT NULL COMMENT '更新用户',
    PRIMARY KEY (`id`),
    KEY `t_bot_user_user_id_IDX` (`user_id`, `bot_key`) USING BTREE
) ENGINE = InnoDB
  DEFAULT CHARSET = utf8mb4 COMMENT ='机器人和用户的关系';




